import React from "react";
import Navbar from "react-bootstrap/Navbar";
import { ReactComponent as Logo } from "../media/logo.svg";
import { Container, Form } from "react-bootstrap";

const navbarStyle = {
  backgroundColor: "#ffffff",
  boxShadow: "0px 8px 8px -6px rgba(0,0,0,.15)",
  height: "4rem"
};

const Header = ({ title, word, setWord, handleSubmit }) => {
  return (
    <Navbar id="nav" variant="light">
      <Container style={{ justifyContent: 'space-around' }}>
        <Logo style={{ width: "26rem", maxHeight: "3.5rem", alignItems: 'center' }} alt={title} />
        <Form onSubmit={handleSubmit} style={{ width: "40rem" }}>
          <Form.Control
            id="search" 
            type="text"
            value={word}
            onChange={(e) => setWord(e.target.value)}
            placeholder="Search free unsplash photos"
          />
        </Form>
      </Container>
    </Navbar>
  );
};

export default Header;
